    private void btnTirarDadosActionPerformed(java.awt.event.ActionEvent evt) {                                              
        //creamos array de botones
        JButton[] botones = {btnDado1, btnDado2, btnDado3, btnDado4, btnDado5, btnDado6};
        //creamos objeto aleaotrio
        Random aleatorio = new Random();
        //esto entra primero, se fija si cumple lo de la bandera
        //el for va pasando por el arreglo, y crea un valor y se lo coloca al dado
        //al terminar con el arreglo baja un numero de las tiradas/banderas q queda
        if (banderaTiradas > 0) {
            for (int i = 0; i < botones.length; i++) {
                if (!retenido[i]) { // solo cambia si NO está retenido
                    int valor = aleatorio.nextInt(6) + 1;
                    botones[i].setText("" + valor);
                }
            }
            //esto cambia el numero de tiradas con texto
            banderaTiradas--;
            labelDeTiradas.setText("Dados ------------------------Tiradas que quedan: " + banderaTiradas);
            
            
        }


    }                                             
