    //esto hace q si le das a calcular no se rompa, ya q estamos transformando el texto en numero, pero si no hay nada se rompe
    //si no tiene nada retorna un 0, usando try catch
    private int parseOrZero(String texto) {
        if (texto == null || texto.trim().isEmpty()) {
            return 0;
        }
        try {
            return Integer.parseInt(texto.trim());
        } catch (NumberFormatException e) {
            return 0;
        }
    }
    private void btnCalcularSumatoriaActionPerformed(java.awt.event.ActionEvent evt) {                                                     
        //calcular la suma de todo con lo q esta arriba
        int numero1 = parseOrZero(txtJugador1.getText());
        int numero2 = parseOrZero(txtJugador2.getText());
        int numero3 = parseOrZero(txtJugador3.getText());
        int numero4 = parseOrZero(txtJugador4.getText());
        int numero5 = parseOrZero(txtJugador5.getText());
        int numero6 = parseOrZero(txtJugador6.getText());
        int numeropoker = parseOrZero(txtJugadorPoker.getText());
        int numeroescalera = parseOrZero(txtJugadorEscalera.getText());
        int numerofull = parseOrZero(txtJugadorFull.getText());
        int numeroGenerala = parseOrZero(txtJugadorGenerala.getText());

        int numero12 = parseOrZero(txtCpu1.getText());
        int numero22 = parseOrZero(txtCpu2.getText());
        int numero32 = parseOrZero(txtCpu3.getText());
        int numero42 = parseOrZero(txtCpu4.getText());
        int numero52 = parseOrZero(txtCpu5.getText());
        int numero62 = parseOrZero(txtCpu6.getText());
        int numeropoker2 = parseOrZero(txtCpuPoker.getText());
        int numeroescalera2 = parseOrZero(txtCpuEscalera.getText());
        int numerofull2 = parseOrZero(txtCpuFull.getText());
        int numeroGenerala2 = parseOrZero(txtCpuGenerala.getText());

        int resultadoJugador1 = numero1 + numero2 + numero3 + numero4 + numero5 + numero6 + numeropoker + numeroescalera + numerofull + numeroGenerala;
        int resultadoCpu1 = numero12 + numero22 + numero32 + numero42 + numero52 + numero62 + numeropoker2 + numeroescalera2 + numerofull2 + numeroGenerala2;
        labelResultadoes.setText("Los resultados son: " + resultadoJugador1 + " Resultado de la Cpu es:" + resultadoCpu1);
    }                                                    
